mapper.update:
update c_user
SET
  state = #{state}
where id = #{id}

mapper.select:
select id, loginname, email from c_user

    state >= 0
    
        AND email = #{email}
    
    
        AND loginname = #{loginname}

select id, loginname, nickname, mobile, email, depart_ment, role_code AS roleCode from c_user
where state = 1 and
id in
  (
    select owner_id
    from job_tasks_owner
    
        job_id = #{jobId}
        and iscancel = 0
        
            and type = #{type}
        
    
  )

select id, loginname, nickname, mobile, email, depart_ment, role_code AS roleCode from c_user
where state = 1 and
id in
(
select owner_id
from b_nodes_owner

    node_id = #{nodeId}
    and iscancel = 0
    
        and type = #{type}
    

)
