mapper.delete:
delete from c_data_authority 
    where object_table = #{objectTable} and object_id = #{objectId}
    
         and owner_id = #{userId} 
    
    
        and type = #{type}

mapper.select:
SELECT id, loginname, email, mobile, nickname from c_user
WHERE id in (
    SELECT t.owner_id FROM c_data_authority t WHERE LOWER(t.object_table) = LOWER(#{objectName}) AND t.object_id= #{objectId}
    
        AND type = #{type}
    
)
