mapper.update:
UPDATE b_data_table
SET iscancel = 1
WHERE id = #{id}

mapper.select:
select count(id) from b_data_table

    iscancel = 0
    
        and bank_name LIKE CONCAT('%',#{bankName},'%')
    
    
        and create_time > #{beginTime}
    
    
        and create_time < #{endTime}

SELECT b.id, b.source_id AS sourceId, b.data_type AS dataType, b.bank_name AS bankName, b.table_name AS tableName, b.create_time AS createTime, d.name AS sourceName from b_data_table b left join b_data_source d on b.source_id = d.id
b.iscancel = 0
and b.bank_name LIKE CONCAT('%',#{bankName},'%')
and b.create_time > #{beginTime}
and b.create_time < #{endTime}
order by b.create_time desc limit #{page.offset},#{page.pageSize}
