mapper.select:
select count(id) from ${nowTableName}
iscancel = 0
and ip_adress LIKE CONCAT('%',#{ipAddress},'%')
and state = #{state}
and (node_id in (select p.node_id from b_nodes_owner p where p.owner_id = #{roleDataControl.userId} and p.owner_level = 1 and iscancel = 0) or job_id in (select t.job_id from job_tasks_owner t where t.owner_id = #{roleDataControl.userId} and t.owner_level = 1 and iscancel = 0))

select id, node_id AS nodeId, job_id AS jobId, job_name AS jobName, ip_adress AS ipAdress, log_date AS logDate, log_title AS logTitle, log_content AS logContent from ${nowTableName}
iscancel = 0
and ip_adress LIKE CONCAT('%',#{ipAddress},'%')
and state = #{state}
and (node_id in (select p.node_id from b_nodes_owner p where p.owner_id = #{roleDataControl.userId} and p.owner_level = 1 and iscancel = 0) or job_id in (select t.job_id from job_tasks_owner t where t.owner_id = #{roleDataControl.userId} and t.owner_level = 1 and iscancel = 0))
order by log_date desc limit #{page.offset},#{page.pageSize}
